// Create lookup map
const dataMap = new Map<number, YiDataLite>()
for (const entry of YIJING_DATA) {
  dataMap.set(entry.code, entry)
}

export function getYiData(code: number): YiDataLite | undefined {
  return dataMap.get(code)
}

export function codeToInt(code: string): number {
  return parseInt(code, 2)
}

export function intToCode(num: number): string {
  return num.toString(2).padStart(6, '0')
}

export function intFullToCode(num: number): string {
  return num.toString(2).padStart(12, '0')
}
